def _test_crosses():
    import time
    t = time.time()
    z = [
        tc.atcfid for tc in pac.tc.values()
        if tc.crosses(atl.us_west_coast, landfall=True)      #626
        # if tc.crosses(atl.atl_conus_coast, landfall=True, is_TC=True)        #606
        # if tc.crosses(atl.atl_conus_coast, landfall=True, category=3, is_TC=True)        #606
    ]
    print(len(z), "cyclones")
    print(time.time() - t, "seconds")
    return z

