import requests

def Template_List(customKey,store,cookie):
    url='https://'+store+'/admin/api/channel/whatsapp/template/list'
    headers = {'cookie': cookie}
    body ={"page":1,"pageSize":50,"customKey":customKey}
    r=requests.post(url=url,headers=headers,json=body).json()
    return r

def Template_Detail(customKey,businessKey,templateGroupId,store,cookie):
    url='https://'+store+'/admin/api/channel/whatsapp/template/list/'+customKey
    params={"businessKey":businessKey,"templateGroupId":templateGroupId}
    headers = {"cookie": cookie}
    r=requests.get(url=url,headers=headers,params=params).json()
    return r

